import React from "react";
import { Router, Route, Switch, Redirect } from "react-router-dom";
import { connect } from "react-redux";

import { history } from "../_helpers";
import { alertActions, tokenActions } from "../_actions";
import { PrivateRoute } from "../_components";
import { HomePage } from "../HomePage";
import { LoginPage } from "../LoginPage";
import { RegisterPage } from "../RegisterPage";
import { AffordabilityPage } from "../AffordabilityPage";
import { WelcomePage } from "../WelcomePage";

class App extends React.Component {
  constructor(props) {
    super(props);

    history.listen((location, action) => {
      // clear alert on location change
      this.props.clearAlerts();
    });
  }

  componentDidMount() {
    const { authenticate } = this.props;
    console.log("component did mount of app.jsx invoke get tokens");
    authenticate();
  }

  render() {
    const { alert, isAuthenticating } = this.props;
    return (
      <div className="jumbotron">
        <div className="container">
          <div className="col-sm-8 col-sm-offset-2">
            {alert.message && (
              <div className={`alert ${alert.type}`}>{alert.message}</div>
            )}
            {isAuthenticating && (
              <img src="data:image/gif;base64," />
            )}
            <Router history={history}>
              <Switch>
                <PrivateRoute exact path="/" component={HomePage} />
                <Route path="/welcome" component={WelcomePage} />
                <Route path="/login" component={LoginPage} />
                <Route path="/register" component={RegisterPage} />
                <Route path="/affordability" component={AffordabilityPage} />
                <Redirect from="*" to="/" />
              </Switch>
            </Router>
          </div>
        </div>
      </div>
    );
  }
}

function mapState(state) {
  const { alert, token = {} } = state;
  const { isAuthenticating = false } = token;
  return { alert, isAuthenticating };
}

const actionCreators = {
  clearAlerts: alertActions.clear,
  authenticate: tokenActions.authenticateToken,
};

const connectedApp = connect(mapState, actionCreators)(App);
export { connectedApp as App };
